    private void btnCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularActionPerformed
        // TODO add your handling code here:
        double n1 = Double.parseDouble(txtN1.getText());
        double n2 = Double.parseDouble(txtN2.getText());

        double suma = n1 + n2;
        double resta = n1 - n2;
        double multiplicacion = n1 * n2;
        double division = n1 / n2;
        double residuo = n1 % n2;
        double potencia = Math.pow(n1, n2);
        double radicacion = Math.pow(n1, (1/n2));

        txtResultado.append("Resultado de la suma: "+suma);
        txtResultado.append("\n");
        txtResultado.append("Resultado de la resta: "+resta);
        txtResultado.append("\n");
        txtResultado.append("Resultado de la multiplicacion: "+multiplicacion);
        txtResultado.append("\n");
        txtResultado.append("Resultado de la division: "+division);
        txtResultado.append("\n");
        txtResultado.append("Resultado del residuo: "+residuo);
        txtResultado.append("\n");
        txtResultado.append("Resultado de la potencia: "+potencia);
        txtResultado.append("\n");
        txtResultado.append("Resultado de la radicacion: "+radicacion);

    }//GEN-LAST:event_btnCalcularActionPerformed

    private void btnIntercambiarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnIntercambiarActionPerformed
        // TODO add your handling code here:
        double n1 = Double.parseDouble(txtN2.getText());
        double n2 = Double.parseDouble(txtN1.getText());

        double suma = n1 + n2;
        double resta = n1 - n2;
        double multiplicacion = n1 * n2;
        double division = n1 / n2;
        double residuo = n1 % n2;
        double potencia = Math.pow(n1, n2);
        double radicacion = Math.pow(n1, (1/n2));
        
        txtResultado.append("\n");
        txtResultado.append("Invirtiendo los numeros......");
        txtResultado.append("Resultado de la suma: "+suma);
        txtResultado.append("\n");
        txtResultado.append("Resultado de la resta: "+resta);
        txtResultado.append("\n");
        txtResultado.append("Resultado de la multiplicacion: "+multiplicacion);
        txtResultado.append("\n");
        txtResultado.append("Resultado de la division: "+division);
        txtResultado.append("\n");
        txtResultado.append("Resultado del residuo: "+residuo);
        txtResultado.append("\n");
        txtResultado.append("Resultado de la potencia: "+potencia);
        txtResultado.append("\n");
        txtResultado.append("Resultado de la radicacion: "+radicacion);
    }//GEN-LAST:event_btnIntercambiarActionPerformed


